INSERT INTO `product` VALUES ('52', 'MECA N 负载TN1-3(DC-3G)', 'MECA N Termination TN1-3(DC-3G)', 'TN1-3', '', '0.00', '生成绝对定位的元素，相对于 static 定位以外的第一个父元素进行定位。\r\n元素的位置通过 \"left\", \"top\", \"right\" 以及 \"bottom\" 属性进行规定', '', '<div class=\"Ma_Bos\">\r\n	<p>\r\n		<span style=\"font-size:16px;\"><strong>MECA负载TN1-3</strong></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:16px;\">型号：TN1-3<br />\r\n连接器形式：N-Male<br />\r\n平均功率(W)：1<br />\r\n峰值功率(W)：1000<br />\r\n频率范围(GHz)：DC-3.0<br />\r\n驻波比(Max)：1.10:1<br />\r\n长度(Inches)：0.990<br />\r\n直径(Inches)：0.82<br />\r\n重量(Ounces)：1.30<br />\r\n阻抗：50 <br />\r\nohms<br />\r\n操作温度：-55℃to +85℃</span> \r\n	</p>\r\n</div>', '1469601986', '0', '7', '25', '37', '61');
